  name: string;
  description: string;
  language: string;
  stars: string;
  starsThisPeriod: string;
  url: string;
}

/**
 * 技术新闻条目
 */
export interface TechNewsItem {
  id: string;
  title: string;
  url: string;
  source: string; // 'hackernews', 'reddit', etc.
  points?: number;
  comments?: number;
  timestamp: string;
  tags: string[];
}

/**
